<?phpinclude './Connection.php';include './phpqrcode/qrlib.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){    $text = $_POST['Text'];    $path = 'Images/';// path of the image dir dito mapupunta yong mga qr code    $qrcode = $path.time().".png";    $qr_image = time().".png";    $Query = "INSERT INTO qrcode(qrtext, qrimage) VALUES(?,?)";    $stmt = $conn->prepare($Query);    $stmt->bind_param('ss',$text,$qr_image);    if($stmt->execute()){        echo 'successfully Generated QrCode';        QRcode :: png($text, $qrcode, 'H',4 , 4);    }else{       echo 'error';    }}